import React, { useState } from "react";
import { useBasket } from "../context/BasketContext";
import { useNavigate } from "react-router-dom";

/**
 * BasketPage Component
 *
 * API Integration Notes:
 * - To integrate with real backend, replace the mock functions with:
 *   - POST /api/basket/add - Add item to basket
 *   - DELETE /api/basket/{id} - Remove item from basket
 *   - POST /api/basket/checkout - Confirm reservation
 *
 * Example API call:
 * const handleConfirm = async () => {
 *   try {
 *     const response = await fetch('/api/basket/checkout', {
 *       method: 'POST',
 *       headers: { 'Content-Type': 'application/json' },
 *       body: JSON.stringify({ items: basket })
 *     });
 *     if (response.ok) {
 *       clearBasket();
 *       navigate("/basket/confirmation");
 *     }
 *   } catch (error) {
 *     console.error('Checkout failed:', error);
 *   }
 * };
 */

const BasketPage = () => {
  const { basket, removeFromBasket, clearBasket, addToBasket } = useBasket();
  const navigate = useNavigate();
  const [newItem, setNewItem] = useState({ destination: "", price: "" });

  const handleAddItem = (e) => {
    e.preventDefault();
    if (newItem.destination && newItem.price) {
      addToBasket({
        id: Date.now(),
        destination: newItem.destination,
        price: Number(newItem.price),
      });
      setNewItem({ destination: "", price: "" });
    }
  };

  const totalPrice = basket.reduce((sum, item) => sum + item.price, 0);

  const handleConfirm = () => {
    // For now, just clear the basket and navigate to confirmation
    clearBasket();
    navigate("/basket/confirmation");
  };

  return (
    <div style={{ maxWidth: "800px", margin: "0 auto", padding: "20px" }}>
      <h1 style={{ textAlign: "center", color: "#333" }}>Your Travel Basket</h1>

      {/* Add Item Form */}
      <form
        onSubmit={handleAddItem}
        style={{
          marginBottom: "20px",
          padding: "20px",
          backgroundColor: "#f9f9f9",
          borderRadius: "8px",
        }}
      >
        <h2 style={{ marginBottom: "15px" }}>Add New Destination</h2>
        <div style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
          <input
            type="text"
            placeholder="Destination"
            value={newItem.destination}
            onChange={(e) =>
              setNewItem((prev) => ({ ...prev, destination: e.target.value }))
            }
            style={{ padding: "8px", flex: 1 }}
          />
          <input
            type="number"
            placeholder="Price"
            value={newItem.price}
            onChange={(e) =>
              setNewItem((prev) => ({ ...prev, price: e.target.value }))
            }
            style={{ padding: "8px", width: "120px" }}
          />
          <button
            type="submit"
            style={{
              padding: "8px 16px",
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Add
          </button>
        </div>
      </form>

      {basket.length === 0 ? (
        <p style={{ textAlign: "center" }}>Your basket is empty.</p>
      ) : (
        <>
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              marginBottom: "20px",
            }}
          >
            <thead>
              <tr style={{ backgroundColor: "#f5f5f5" }}>
                <th
                  style={{
                    padding: "12px",
                    textAlign: "left",
                    borderBottom: "2px solid #ddd",
                  }}
                >
                  Destination
                </th>
                <th
                  style={{
                    padding: "12px",
                    textAlign: "left",
                    borderBottom: "2px solid #ddd",
                  }}
                >
                  Price
                </th>
                <th
                  style={{
                    padding: "12px",
                    textAlign: "left",
                    borderBottom: "2px solid #ddd",
                  }}
                >
                  Action
                </th>
              </tr>
            </thead>
            <tbody>
              {basket.map((item) => (
                <tr key={item.id} style={{ borderBottom: "1px solid #ddd" }}>
                  <td style={{ padding: "12px" }}>{item.destination}</td>
                  <td style={{ padding: "12px" }}>${item.price}</td>
                  <td style={{ padding: "12px" }}>
                    <button
                      onClick={() => removeFromBasket(item.id)}
                      style={{
                        padding: "8px 16px",
                        backgroundColor: "#ff4444",
                        color: "white",
                        border: "none",
                        borderRadius: "4px",
                        cursor: "pointer",
                      }}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div style={{ textAlign: "right", marginBottom: "20px" }}>
            <p style={{ fontSize: "1.2em", fontWeight: "bold" }}>
              Total: ${totalPrice}
            </p>
          </div>
          <div style={{ textAlign: "center" }}>
            <button
              onClick={handleConfirm}
              style={{
                padding: "12px 24px",
                backgroundColor: "#4CAF50",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
                fontSize: "1.1em",
              }}
            >
              Confirm Reservation
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default BasketPage;
