import React from "react";
import { useBasket } from "../context/BasketContext";
import { useNavigate } from "react-router-dom";

const BasketPage = () => {
  const { basket, removeFromBasket, clearBasket } = useBasket();
  const navigate = useNavigate();

  const totalPrice = basket.reduce((sum, item) => sum + item.price, 0);

  const handleConfirm = () => {
    // Mock API call or real endpoint call here
    // For now, just clear the basket and navigate to confirmation
    clearBasket();
    navigate("/basket/confirmation");
  };

  return (
    <div style={{ maxWidth: "800px", margin: "0 auto", padding: "20px" }}>
      <h1 style={{ textAlign: "center", color: "#333" }}>Your Travel Basket</h1>
      {basket.length === 0 ? (
        <p style={{ textAlign: "center" }}>Your basket is empty.</p>
      ) : (
        <>
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              marginBottom: "20px",
            }}
          >
            <thead>
              <tr style={{ backgroundColor: "#f5f5f5" }}>
                <th
                  style={{
                    padding: "12px",
                    textAlign: "left",
                    borderBottom: "2px solid #ddd",
                  }}
                >
                  Destination
                </th>
                <th
                  style={{
                    padding: "12px",
                    textAlign: "left",
                    borderBottom: "2px solid #ddd",
                  }}
                >
                  Price
                </th>
                <th
                  style={{
                    padding: "12px",
                    textAlign: "left",
                    borderBottom: "2px solid #ddd",
                  }}
                >
                  Action
                </th>
              </tr>
            </thead>
            <tbody>
              {basket.map((item) => (
                <tr key={item.id} style={{ borderBottom: "1px solid #ddd" }}>
                  <td style={{ padding: "12px" }}>{item.destination}</td>
                  <td style={{ padding: "12px" }}>${item.price}</td>
                  <td style={{ padding: "12px" }}>
                    <button
                      onClick={() => removeFromBasket(item.id)}
                      style={{
                        padding: "8px 16px",
                        backgroundColor: "#ff4444",
                        color: "white",
                        border: "none",
                        borderRadius: "4px",
                        cursor: "pointer",
                      }}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div style={{ textAlign: "right", marginBottom: "20px" }}>
            <p style={{ fontSize: "1.2em", fontWeight: "bold" }}>
              Total: ${totalPrice}
            </p>
          </div>
          <div style={{ textAlign: "center" }}>
            <button
              onClick={handleConfirm}
              style={{
                padding: "12px 24px",
                backgroundColor: "#4CAF50",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
                fontSize: "1.1em",
              }}
            >
              Confirm Reservation
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default BasketPage;
