import React from "react";
import { useBasket } from "../context/BasketContext";
import { useNavigate } from "react-router-dom";

const BasketPage = () => {
  const { basket, removeFromBasket, clearBasket } = useBasket();
  const navigate = useNavigate();

  const totalPrice = basket.reduce((sum, item) => sum + item.price, 0);

  const handleConfirm = () => {
    // Mock API call or real endpoint call here
    // For now, just clear the basket and navigate to confirmation
    clearBasket();
    navigate("/basket/confirmation");
  };

  return (
    <div>
      <h1>Your Travel Basket</h1>
      {basket.length === 0 ? (
        <p>Your basket is empty.</p>
      ) : (
        <>
          <table>
            <thead>
              <tr>
                <th>Destination</th>
                <th>Price</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {basket.map((item) => (
                <tr key={item.id}>
                  <td>{item.destination}</td>
                  <td>${item.price}</td>
                  <td>
                    <button onClick={() => removeFromBasket(item.id)}>
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <p>Total: ${totalPrice}</p>
          <button onClick={handleConfirm}>Confirm Reservation</button>
        </>
      )}
    </div>
  );
};

export default BasketPage;
